import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  width: 600px;
  height: 600px;
  display: flex;
  flex-wrap: wrap;
  border: 5px solid grey; /* Agregamos borde al contenedor */
`;

const Quadrant = styled.div`
  box-sizing: border-box;
  width: 50%;
  height: 50%;
  padding: 10px;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;

  /* Estilo para la imagen */
  & > img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

/* Primer cuadrante */
const FirstQuadrant = styled(Quadrant)`
  margin: 5px 2.5px 2.5px 5px;
`;

/* Resto de los cuadrantes */
const OtherQuadrant = styled(Quadrant)`
  border: 2.5px solid grey;
  border-top: none;
  border-left: none;

  /* Estilo específico para el cuadrante de la esquina superior derecha */
  &:first-child {
    border-left: 2.5px solid grey;
    border-top: 2.5px solid grey;
  }

  /* Estilo específico para el cuadrante de la esquina inferior izquierda */
  &:nth-child(3) {
    border-bottom: 2.5px solid grey;
    border-left: 2.5px solid grey;
  }

  /* Estilo específico para el cuadrante de la esquina inferior derecha */
  &:last-child {
    border-bottom: 2.5px solid grey;
    border-right: 2.5px solid grey;
  }
`;

const App = () => {
  const [images, setImages] = useState<string[]>([]);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);

    const urls = files.map((file) => URL.createObjectURL(file));

    setImages(urls);
  };

  return (
    <>
      <input type="file" multiple onChange={handleFileUpload} />
      <Container>
      <FirstQuadrant>{images[0] && <img src={images[0]} alt="imagen1" />}</FirstQuadrant>
        <OtherQuadrant>{images[1] && <img src={images[1]} alt="imagen2" />}</OtherQuadrant>
        <OtherQuadrant>{images[2] && <img src={images[2]} alt="imagen3" />}</OtherQuadrant>
        <OtherQuadrant>{images[3] && <img src={images[3]} alt="imagen4" />}</OtherQuadrant>
      </Container>
    </>
  );
};

export default App;
