import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  width: 600px;
  height: 600px;
  display: flex;
  flex-wrap: wrap;
`;

const Quadrant = styled.div`
  box-sizing: border-box;
  width: 50%;
  height: 50%;
  border: 5px solid grey;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FirstQuadrant = styled.div`
  box-sizing: border-box;
  width: 50%;
  height: 50%;
  border-top: 5px solid grey;
  border-left: 5px solid grey;
  display: flex;
  justify-content: center;
  align-items: center;
  border-bottom: 2.5px solid grey;
  border-right: 2.5px solid grey;
`;

const SecondQuadrant = styled.div`
  box-sizing: border-box;
  width: 50%;
  height: 50%;
  border-top: 5px solid grey;
  border-left: 2.5px solid grey;
  display: flex;
  justify-content: center;
  align-items: center;
  border-bottom: 2.5px solid grey;
  border-right: 5px solid grey;
`;

const Third = styled.div`
  box-sizing: border-box;
  width: 50%;
  height: 50%;
  border-top: 2.5px solid grey;
  border-left: 5px solid grey;
  display: flex;
  justify-content: center;
  align-items: center;
  border-bottom: 5px solid grey;
  border-right: 2.5px solid grey;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const App = () => {
  const [images, setImages] = useState<string[]>([]);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);

    const urls = files.map((file) => URL.createObjectURL(file));

    setImages(urls);
  };

  return (
    <>
      <input type="file" multiple onChange={handleFileUpload} />
      <Container>
        <FirstQuadrant>{images[0] && <Image src={images[0]} alt="image 1" />}</FirstQuadrant>
        <SecondQuadrant>{images[1] && <Image src={images[1]} alt="image 2" />}</SecondQuadrant>
        <Third>{images[2] && <Image src={images[2]} alt="image 3" />}</Third>
        <Quadrant>{images[3] && <Image src={images[3]} alt="image 4" />}</Quadrant>
      </Container>
    </>
  );
};

export default App;
