import React, { useState } from "react";
import ImageFile from "react-image-file";

const ImageLoader: React.FC = () => {
  const [images, setImages] = useState<Array<string>>([]);

  const handleImageLoad = (imageDataUrl: string) => {
    if (images.length < 4) {
      setImages([...images, imageDataUrl]);
    }
  };

  return (
    <div style={{ display: "grid", gridTemplateColumns: "repeat(2, 1fr)", gridGap: "10px", width: "500px", height: "500px" }}>
      {images.map((image, index) => (
        <div key={index} style={{ width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center", border: "1px solid black" }}>
          <img src={image} alt={`Image ${index}`} style={{ maxWidth: "100%", maxHeight: "100%" }} />
        </div>
      ))}
      {[...Array(4 - images.length)].map((_, index) => (
        <ImageFile
          key={index}
          label={`Select image ${images.length + index + 1}`}
          imagePreviewProps={{ style: { width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center", border: "1px solid black" } }}
          onLoad={handleImageLoad}
        />
      ))}
    </div>
  );
};

export default ImageLoader;
