import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [images, setImages] = useState<(string | null)[]>([null, null, null, null]);

  const handleImageChange = (event: React.ChangeEvent<HTMLInputElement>, index: number) => {
    const file = event.target.files && event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onloadend = () => {
        setImages((prevImages) => {
          const newImages = [...prevImages];
          newImages[index] = reader.result as string;
          return newImages;
        });
      };
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-6 col-md-3">
          <div className="image-container">
            {images[0] ? <img src={images[0]} alt="imagen1" /> : <p>No hay imagen</p>}
          </div>
          <input type="file" accept="image/*" onChange={(event) => handleImageChange(event, 0)} />
        </div>
        <div className="col-6 col-md-3">
          <div className="image-container">
            {images[1] ? <img src={images[1]} alt="imagen2" /> : <p>No hay imagen</p>}
          </div>
          <input type="file" accept="image/*" onChange={(event) => handleImageChange(event, 1)} />
        </div>
        <div className="col-6 col-md-3">
          <div className="image-container">
            {images[2] ? <img src={images[2]} alt="imagen3" /> : <p>No hay imagen</p>}
          </div>
          <input type="file" accept="image/*" onChange={(event) => handleImageChange(event, 2)} />
        </div>
        <div className="col-6 col-md-3">
          <div className="image-container">
            {images[3] ? <img src={images[3]} alt="imagen4" /> : <p>No hay imagen</p>}
          </div>
          <input type="file" accept="image/*" onChange={(event) => handleImageChange(event, 3)} />
        </div>
      </div>
    </div>
  );
};

export default App;
