import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  width: 600px;
  height: 600px;
  display: flex;
  flex-wrap: wrap;
`;

const Quadrant = styled.div`
  box-sizing: border-box;
  width: 50%;
  height: 50%;
  padding: 10px;
  border: 5px solid grey;
  position: relative; /* Agregamos una posición relativa para poder utilizar position absolute en ::after */
  display: flex;
  justify-content: center;
  align-items: center;

  /* Estilo para el pseudo-elemento ::after */
  &::after {
    content: "";
    position: absolute;
    border: 5px solid grey;
    width: 50%;
    height: 50%;
  }

  /* Estilo específico para los cuadrantes centrales */
  &:nth-child(2n-1) {
    &::after {
      border-top: none; /* Eliminamos la mitad del borde superior */
      border-bottom: none; /* Eliminamos la mitad del borde inferior */
    }
  }

  &:nth-child(-n+2):last-child, &:nth-child(n+3):last-child {
    &::after {
      border-left: none; /* Eliminamos la mitad del borde izquierdo */
      border-right: none; /* Eliminamos la mitad del borde derecho */
    }
  }
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const App = () => {
  const [images, setImages] = useState<string[]>([]);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(event.target.files || []);

    const urls = files.map((file) => URL.createObjectURL(file));

    setImages(urls);
  };

  return (
    <>
      <input type="file" multiple onChange={handleFileUpload} />
      <Container>
        <Quadrant>{images[0] && <Image src={images[0]} alt="image 1" />}</Quadrant>
        <Quadrant>{images[1] && <Image src={images[1]} alt="image 2" />}</Quadrant>
        <Quadrant>{images[2] && <Image src={images[2]} alt="image 3" />}</Quadrant>
        <Quadrant>{images[3] && <Image src={images[3]} alt="image 4" />}</Quadrant>
      </Container>
    </>
  );
};

export default App;
