import React, { useState } from "react";
import SearchBar from "./Searchbox";
import { Container, Row, Col, Image } from 'react-bootstrap';

interface UnsplashResult {
  id: string;
  src: {
    regular: string;
    small: string
  };
  url:string;
  alt_description: string;
}

function ImageSearch() {
  const [images, setImages] = useState<UnsplashResult[]>([]);

  const handleSearch = (query : string) => {
    fetch(`https://api.pexels.com/v1/search?query=${query}&width=1000&height=1000&orientation=square`, {
      headers: {
        Authorization: "M2ACr5bAJ92wsqvkyvdhI1IP3xiEbaX4jE2N5KBwVDWtCGgTdSDWy72a",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setImages(data.photos);

        console.log(data.photos);
      });
  };

  return (
    <div>
      <SearchBar onSubmit={handleSearch} />
      <Container>
      <Row>
        {images.map(image => (
          <Col key={image.id} md={4}>
            <Image src={image.src.small} alt={image.alt_description} thumbnail />
          </Col>
        ))}
      </Row>
    </Container>
    </div>
  );
}

export default ImageSearch;
