import React, { useState } from "react";
import Dropzone, { DropzoneState, FileWithPath } from "react-dropzone";

import "./App.css";

type Accept = string | string[];

function App(): JSX.Element {
  const [images, setImages] = useState<string[]>([]);

  const onDrop = (acceptedFiles: FileWithPath[]) => {
    const updatedImages = [...images];
    acceptedFiles.forEach((file: FileWithPath) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        const imageDataUrl = reader.result as string;
        updatedImages.push(imageDataUrl);
        setImages(updatedImages.slice(0, 4)); // limit to 4 images
      };
    });
  };

  const accept: Accept = "image/*";

  return (
    <div className="App">
      <Dropzone onDrop={onDrop} accept={accept} maxFiles={4}>
        {({ getRootProps, getInputProps }: DropzoneState) => (
          <div {...getRootProps()} className="dropzone">
            <input {...getInputProps()} />
            <p>Arrastra y suelta imágenes aquí, o haz clic para seleccionar</p>
          </div>
        )}
      </Dropzone>
      <div className="image-container">
        {images.map((image: string) => (
          <div className="image-wrapper" key={image}>
            <img src={image} alt="uploaded" />
          </div>
        ))}
        {images.length < 4 && (
          <div className="placeholder">
            {`Arrastra y suelta hasta ${4 - images.length} más`}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
